const transition = {
  transition: "all 0.33s cubic-bezier(0.685, 0.0473, 0.346, 1)"
};

const boxShadow = {
  boxShadow:
    '0px 1px 3px 0px rgba(0, 0, 0, 0.2), 0px 1px 1px 0px rgba(0, 0, 0, 0.14), 0px 2px 1px -1px rgba(0, 0, 0, 0.12)'
};

const text = {
  fontFamily: '"Barlow Semi Condensed", sans-serif'
};

const sectionTitle = {
  fontFamily: '"Barlow Semi Condensed", sans-serif',
  color: '#26213b',
  fontStyle: 'italic',
  letterSpacing: '1.9px',
}

const fullWidthSection = {
  maxWidth: '1920px',
  overflow: 'hidden',
  width: '100%',
  margin: '6em 0 0',
  fontFamily: '"Barlow Semi Condensed", sans-serif',
}

const sectionContainer = {
  maxWidth: '1920px',
  overflow: 'hidden',
  width: '85%',
  margin: '6em auto 0',
  fontFamily: '"Barlow Semi Condensed", sans-serif',
  paddingBottom: '4em',
  borderBottom: '1px solid #e2daf4'
}

const imgResponsive = {
  width: '100%',
  height: 'auto'
}

export {
  //variables
  transition,
  boxShadow,
  text,
  sectionTitle,
  sectionContainer,
  fullWidthSection,
  imgResponsive
}
