import { boxShadow, transition } from '../general-styles.js';
const horizontalCardStyles = {
  cardContainer:{
    display: 'flex',
    ...boxShadow,
    background: '#fcfcfc',
    marginBottom: '2em',
    borderRadius: '3px',
    maxWidth: '1000px',
  },
  fotoContainer:{
    '&:hover':{

    }
  },
  foto: {
    backgroundSize: 'cover',
    backgroundPosition: 'center top',
    height: '100%',
    width: '500px',
    ...transition,
    '&:hover':{
        backgroundSize: 'contain'
    }
  },
  textContainer:{
    padding: '2em 5em 5em 3em'
  },
  name: {
    fontWeight: '800',
    fontSize: '4rem',
    margin: '0',
    color: '#ddd',
  },
  description:{
    fontSize: '1.2rem',
    lineHeight: '1.5',
    color: '#666',
    fontWeight: '500',
  },
  extraList:{
    fontSize: '1rem',
    color: '#666',
    marginLeft: '-15px',
    textTransform: 'uppercase',
    letterSpacing: '7px',
    fontWeight: '300',
    lineHeight: '1.8',
    marginTop: '2rem',
  }



}

export default horizontalCardStyles;
