import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import horizontalCardStyles from '../styles/horizontalCardStyles';
import pic1 from '../images/student1.png';
import pic2 from '../images/student2.jpg';
import pic3 from '../images/student3.png';

const HorizontalCard = (props) => {
  const { classes } = props;
  return(
    <div className={classes.cardContainer}>
      <div className={classes.fotoContainer}>
        <img src={pic1} />
      </div>
      <div className={classes.textContainer}>
        <p className={classes.name}>{props.name}</p>
        <p className={classes.description}>{props.description}</p>
        <div>
        {props.extra.map((extra, i) => (
          <span key={i}>{extra}</span>
        ))}
        </div>
      </div>
    </div>

  )
}

export default withStyles(horizontalCardStyles)(HorizontalCard);
