import React from 'react';
import PropTypes from 'prop-types';
// import with local styles
import { withStyles } from '@material-ui/core/styles';
// @material-ui/core components
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Hidden from "@material-ui/core/Hidden";
// local components
import menuStyles from '../styles/menuStyles';
import logo from '../images/logo.svg'

class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mobileOpen: false
    };
    this.handleDrawerToggle = this.handleDrawerToggle.bind(this);
  }
  handleDrawerToggle() {
    this.setState({ mobileOpen: !this.state.mobileOpen });
  }

  render() {
    const {classes} = this.props;
    return (
      <div className={classes.root}>
        <AppBar position="absolute" className={classes.menu}>
        <Hidden smDown implementation="css">
          <Toolbar>
            <IconButton className={classes.menuButton} color="inherit" aria-label="Menu">
              <MenuIcon />
            </IconButton>

            <IconButton className={classes.logo} color="inherit" aria-label="Menu">
              <img src={logo} />
            </IconButton>
            <Button color="inherit" className={classes.menuButton}>Courses</Button>
            <Button color="inherit" className={classes.menuButton}>Company</Button>
            <Button color="inherit" className={classes.menuButton}>Login</Button>
          </Toolbar>
          </Hidden>
          <Hidden mdUp implementation="css">
            <MenuMobile />
          </Hidden>
        </AppBar>
      </div>
    );
  }
}

Menu.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(menuStyles)(Menu);
