import { boxShadow } from '../general-styles.js';
const horizontalCardStyles = {
  cardContainer:{
    display: 'flex',
    ...boxShadow,
    background: '#fcfcfc',
    marginBottom: '2em',
    borderRadius: '3px',
  },
  fotoContainer:{
    width: '500px',
  },
  foto: {
    backgroundSize: 'cover',
    backgroundPosition: 'center center',
    height: '100%',
  },
  textContainer:{
    padding: '2em 5em 5em 3em'
  },
  name: {
    fontWeight: '800',
    fontSize: '4rem',
    margin: '0',
    color: '#ddd',
  },
  description:{
    fontSize: '1.2rem',
    lineHeight: '1.5',
    color: '#25213c',
    fontWeight: '300',
  }


}

export default horizontalCardStyles;
