import React from "react";
// Pacakege for the slider
import Slider from "react-slick";
import { withStyles } from '@material-ui/core/styles';
import sliderStyles from '../styles/sliderStyles';

const SliderComponent = (props) => {
  const {classes} = props;
  var settings = {
      dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 1,
      //autoplay: true,
      autoplaySpeed: 2000,
      pauseOnHover: true
    };
    return (
      <div className={classes.sliderContainer}>

        <Slider {...settings}>
          <div>
            <h3>1</h3>
          </div>
          <div>
            <h3>This course has seriously gotten me off my ass and completely motivated to follow my dreams. I've been brainstorming like a **** and between two channels I got my first 8 videos mapped out... and plenty of ideas for more of the shit I'll be doing. ... Joining Jumpcut Academy has only fueled [my] motivation and made it CRAZY.</h3>
          </div>
          <div>
            <h3>3</h3>
          </div>
          <div>
            <h3>4</h3>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
        </Slider>
      </div>
    );
}


export default withStyles(sliderStyles)(SliderComponent);
