import { imgResponsive, boxShadow } from '../general-styles.js';
const photoBoxStyles = {
  img:{
    ...imgResponsive,
    ...boxShadow,
    borderRadius: '3px',
    position: 'relative',
    '&:hover':{
      opacity: 0.9
    },
    '&:hover::before':{
      animation: 'shine .75s',
      opacity: '0.9'
    },
    '&::before':{
      position: 'absolute',
    	top: '0',
    	left: '-75%',
    	zIndex: '2',
    	display: 'block',
    	content: '" "',
    	width: '50%',
    	height: '100%',
    	background: 'linear-gradient(to right, rgba(255,255,255,0) 0%, rgba(255,255,255,.3) 100%)',
    	transform: 'skewX(-25deg)',
    },
  },
  '@keyframes shine': {
  	'100%': {
  		left: '125%',
  	}
  }
}

export default photoBoxStyles;
