import { boxShadow } from '../general-styles.js';
const horizontalCardStyles = {
  cardContainer:{
    display: 'flex',
    ...boxShadow,
    background: '#fff',
    marginBottom: '2em',
    borderRadius: '3px',
  },
  fotoContainer:{
    width: '500px',
  },
  foto: {
    backgroundSize: 'cover',
    backgroundPosition: 'center center'
  }


}

export default horizontalCardStyles;
