import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import horizontalCardStyles from '../styles/horizontalCardStyles';

const HorizontalCard = (props) => {
  const { classes } = props;
  return(
    <div className={classes.container}>
      <figure className={classes.photoContainer}>
        <img src={props.picture} alt="Successfull Stories Jumpcut" className={classes.img} />
      </figure>
      <div className={classes.footer}>
        <p className={classes.name}>{props.name}</p>
        <p className={classes.description}>{props.description}</p>
      </div>
    </div>

  )
}

export default withStyles(horizontalCardStyles)(HorizontalCard);
